INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','다른 수식어 필요없이 좋다. 골프 하나 배우는 것도 교양과목으로 괜찮을듯.','2012-12-14 08:41:08',9.66667),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','일단 재밌었다. 교수님 자체도 재밌으신 분이고, 골프에 대해 배울 수 있는 좋은 기회였다. 학점이 생각했던 만큼 잘 안나왔지만..ㅠㅠ 마지막 스윙 시험을 내가 잘 못 본 듯 하니 누구를 탓하랴...ㅠㅠ','2012-12-26 05:00:17',9.66667),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','실습평가가 운에 갈린다. 평소에 잘쳐도 시험볼때 못치면 망...','2012-12-26 05:47:27',7.66667),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','1년전에 들었는데ㅋㅋ \r\n우리학교 교양치고 , 나름괜찮다. 1학점이라 부담없고 , 또 다 처음배우는 우리학교 학생들인데 다 초보라서 못쳐도 하나도 안부끄럽다. 그냥 골프가 이런거구나 , 혹은 힘든 학과공부중에 나름 머리를 식힐수있는 교양과목','2013-01-03 17:44:30',8.33333),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','골프간보기 좋은 수업.\r\n전반적으로 만족하는데 성적이.... 에이쁠이었는데 성적확인기간 지나고 다시 보니 삐플로 내력있었다 \r\n그래서 상당히 뭔가 찜찜한...에이플받기는 애매한 성적이었지만 뭐 아무튼 성적은 보장못하지만 재미로 들으면 좋은 수업이다.','2013-01-18 18:44:00',8.66667),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 재밌지만 시험못보면 재밌어도 성적이 재미가 없어진다.','2013-01-23 05:23:48',8.33333),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','그냥 교수가 미친것같다 용어 100개를 다외워서 10개를 낸다니 ','2013-04-09 06:00:08',1),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 김석기 교수님 학점기준이 너무 애매하다 출첵다하고 중간 잘보고 기말도 반3등안에 들었는데 C+나왔다. \r\n여자들한테 분명히 잘줬을거라 장담한다. 여자는 들어라 A 보장이다','2013-06-26 10:04:14',1),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','피봤다.','2013-07-17 02:38:54',1),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','골프 배우는게 재미있었고 그런데 시험 ㅠㅠ','2013-10-23 10:34:39',9),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','골프를 배운적이 있으면 꿀이다 하지만 배운적이 없으면 비추한다 시험이 골프를 치는것인데 실수라도하면 학점 쭉 내려간다','2014-03-19 12:11:34',10),(593,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','C!','2014-07-03 15:05:04',5);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','사람이 너무 많아 수업 듣기 복잡스럽고, 더이상 A+폭격기도, 석기시대도 아니다.\r\n그렇기 때문에 정말 자신에게 도움이 되는 교양을 듣고 싶은 사람이라면 비추.\r\n그냥 편하게 교수님 농담 듣고 이런저런 가끔 유용한 충고 듣고 싶은 사람은 추천.\r\n정확한 성적의 기준을 모르겠지만, 여느 특정과목 같이 확실히 여학우 우대는 있음.','2012-11-15 09:25:23',5.33333),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','최근에는 많이 바뀐것 같음. 저는 이거 2년전에 들었는데....시험 어중간히 적당히 보고 스포츠경기 한번 갔다왔떠니 A+ 받았었음...\r\n근데 지금은 폭격기 아닌듯 ㅋㅋ','2012-11-15 12:04:43',7.66667),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','남는게 없는 교양수업, 수업도 엄청 대충하고, 교수는 책 팔아먹기 바쁨. 본인이 쓴 \'현대스포츠문화의이해\'라는 책은 무려 2만원이나 하고 심지어 교수는 수업시간에 이책을 중고로 사고팔지 말라고 말하기까지함.\r\n대학까지 와서 비싼 등록금내고 이런 교양을 왜 들어야 하는지 모르겠다.','2012-11-19 15:08:53',2.33333),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','에휴..','2012-12-13 23:43:43',1),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','최악!! 배운거 하나도 없음!! 교육이 진짜 별로임','2012-12-14 02:27:52',1),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험범위및 시험문제가 이상함. 주관적 평가가 심함., 쉬는 시간없이 2시간 연강이면서 졸지도 못하게 함.','2012-12-14 03:04:51',6),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 지루하고 몰입이 부족하다고 생각을 합니다.','2012-12-14 06:20:51',4.66667),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','전혀 배우는게 없다','2012-12-14 08:15:05',1),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','스포츠에 대해 더 자세히 알아가는 좋은 수업이었지만 매시간마다 교수님이 늦게 들어오시고 빡빡한 수업 진행은 마음에 안든다\r\n과제는 한학기에 하나만 하면 되는데 그냥 책보고 2단원을 요약하는 것.\r\n시험은 책만 열심히 보면 다 맞을 수 있고 그냥 열심히 하면 된다\r\n수업시간에 자거나 핸드폰 꺼내기만 해도 바로 깎으니까 조심.','2012-12-14 08:20:44',7.66667),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너무 시험범위가 말이 안될 정도로 많고 수업시간도 잘 안지킨다.','2012-12-15 13:18:26',1.66667),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업시간도 잘 안지키시고, 시험시간은 10분이면 충분다면서 시험보는 내내 잔소리하신다.','2012-12-16 06:53:50',4.66667),(602,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 자리도 지정석이고 교수님 엄청 늦고 ppt만 읽고 잡소리 많이하시고 수업 얻는거 없고 10분씩 매일 늦게 끝내주시고 과제는 1~2장 요약해오는건데 그것도 수업안넘어가고 사람들요약한거중에 내고... 참;; 여자들 편애해요 ㅠㅠㅠ;;; 짱나잉 시험 은 그래도 책 몇번읽으면 다 맞출