INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','반도체에 대한 지식도 중요하지만 실제로 그 공정과정을 자세히 아는 것도 중요한데, 바로 이 수업이 공정과정을 자세히 학습할수 있는 수업이다.\r\n교수님께서 학생들에게 보다 더 많이 가르쳐주시려고 하시고 공정과 장비에 대한 기초적 지식을 알기 쉽게 설명해주셔서 너무 재미있게 듣고 있다.\r\n과제 또한 연습문제 위주로 과제를 내주셔서 그 단원을 마치고 복습해 보는 좋은 기회가 될 수 있다. ','2012-11-16 10:47:30',10),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','반도체 공정에 대한 전반적인 디바이스에 많은 도움이 됨 향후 취업시에 면접자료 활용가능 산기대 입학 이후 나노광공학과는 정말 유용한 강의임, 어려운 과정을 쉽고 재밌게 설명해줌','2012-12-11 13:57:24',10),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','나노광공학과 강추하는 강의중하나\r\n수업만 집중있게 들으면 시험은 쉽게 내셔서 성적도 받기 쉬움 ','2013-01-23 05:19:41',9.66667),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','김창규 교수님 만세','2013-01-23 06:23:11',10),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','강의최고!','2013-02-24 04:54:16',10),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','창규쌤 만세 \r\n','2013-08-28 17:39:16',8.66667),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굿','2013-10-28 15:01:34',9),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅎㅎ','2014-12-23 21:15:58',1),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅎㅎ','2014-12-23 21:16:05',1),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅎㅎ','2014-12-23 21:16:12',1),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅎㅎ','2014-12-23 21:16:19',1),(500,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 괜찮다. 나쁜의미는 아니다만, 그런데 그것뿐이다. 공정쪽에 많이 취직하니 필수로 들어야한다고 본다.','2015-06-21 00:24:27',8.33333);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','따로 시험공부 할 필요없이\r\n재미있게 배울 수 있어요\r\n상대평가인 만큼 학생수가 적어 높은 점수를 받으려면 시험을 잘봐야해요\r\n시험은 음..수업 잘 듣고 센스만 좀 있으면 잘볼수있을듯여','2013-01-23 06:25:30',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','e교수님짱 ','2013-01-27 18:22:25',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굳뜨!','2013-02-19 12:07:31',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 저도듣고싶어요 ㅋㅋㅋㅋ','2013-02-24 05:02:41',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굿굿굿','2013-02-28 03:53:13',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진짜 굳뜨\r\n필기는 쉬우나\r\n실기는 쉽지가않다','2013-03-04 05:00:53',10),(1091,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅀ','2013-03-04 10:36:47',1);
