INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정민이형 돌아와요....제발......','2012-12-07 06:42:51',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','\r\nC언어의 입문을 박정민 교수님과 함께 즐겁게 했었다. \r\n개념을 잘 정리해서 머리속에 콕콕 넣어주신다.\r\n필기 해주셔서 나중에 공부할 때 도움이 꽤 된다.\r\n과제는 없었고 시험은 항상 오픈북.\r\n2학기때도 교수님만 믿고 C언어 들으려고 했는데 어디가셨어요..................... 돌아오세요........................','2012-12-09 15:07:05',9.66667),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','돌아오세요ㅡㅜ 동영상 강의만 주고가시면 어케요 ㅡㅜ','2012-12-10 15:45:16',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','재미있고 쉽지만 명쾌한 강의!\r\nc플밍1만 하시고 2학기엔 가버리시면 어찌합니까 ㅜㅜ','2013-03-03 19:52:37',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','-','2013-07-18 01:45:29',6),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','신혼생활에 충실하시길','2013-07-25 09:03:22',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','1학년들은 닥치고 박정민 교수님 플밍들을생각해라','2014-06-07 17:20:11',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정말 괜찮고 좋아요','2014-07-03 06:42:03',9.33333),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','무슨깅?','2014-12-17 15:20:10',8.33333),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','1학년때 박정민 교수님 플밍듣길 정말잘한듯','2014-12-27 09:52:24',9.33333),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','닥치고 박정민 교수님ㄱㄱ','2015-01-04 11:20:50',10),(855,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좋군','2015-07-07 17:03:34',1);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','PPT를 띄워서 열정적으로 수업을 하셔서 도움이 많이 된다.\r\n그리고 연습문제를 과제로 내주셔서 수업복습을 따로 할필요없이 과제를 하기만 하면 수업에 대한 전체적인 이해가 잘된다.\r\n','2012-11-18 12:30:20',7.66667),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 지루한면이 없지않아있지만 과제도 적은편이고 학점도 훈훈하게 주시는 훈훈한 분이시다','2012-11-22 11:57:59',8.33333),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','차근차근 설명하시고, 중요한 부분을 더 열심히 가르치는 모습이 좋다. ','2013-06-15 10:57:26',8.33333),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학생을 생각하시는 수업을 들으며 느껴질정도로\r\n차분하게 알려주시는 면이 좋았고, 수업에 대한 열의(출석, 참여도)를 어느정도 \r\n보여주면 충분히 좋은점수를 주셔서 좋았습니다.','2013-06-20 16:15:47',10),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 좋아요! 목소리톤이 낮은편이라 간혹 지루하게 들릴 수도 있지만 집중해서 잘 들어보면 그 안에 핵심내용이 다 있다. 무턱대고 넓은 시험 범위가지고 공부하라고 하시는 것도 아니고 알아야할 부분만 콕콕 찝어주시니 그 부분에 있어서 집중적으로 공부할 수 있어서 좋았다. 내 생각엔 무작정 시험범위를 방대하게 설정하는 것보다 박종현 교수님 같은 방식이 종강후에도 머리에 뭔가 남는 것 같다. 과제 열심히해가삼. 과제 비중이 상당히 높음!','2013-06-22 11:19:46',10),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 조곤조곤 중요한 부분만 딱 알려주시는 방식 좋았습니다. 과제 비중이 큰 편이에요. 시험도 교수님이 특정해주진 범위에서만 나오기때문에 공부하기도 수월했습니다. 아쉽게 A는 놓쳤지만 화학에 전혀 지식이 없었는데도 잘 따라가게 이끌어주셔서 만족합니다. ','2013-06-30 06:27:17',8.66667),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 정말 지루했습니다.','2013-07-03 07:40:10',5.66667),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 좀 지루한면이있으나 수업시간 성실도를 보시며 A를 40퍼센트까지 주십니다.','2013-07-03 08:46:38',8.33333),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학점 잘 챙겨주심','2013-07-04 01:17:10',1),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학점을 너무 낮게 주셧어요','2013-07-06 04:26:56',6.33333),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 집중이잘안되고 과제는 은근 부담 근데 학점은 잘나옴','2013-07-06 07:48:43',7),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제 내주시는게 단원마다 A4 한페이지 정도 되고\r\n근데 과제에서 시험문제 출제됨.','2013-07-18 01:58:31',7),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','재미없음 독학이더나을듯','2013-09-07 11:34:24',5.33333),(673,1,'벙커에서 작성된 강의평가입니다','www.kpubunk