INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','훌륭한 강의라고 생각합니다. ','2012-11-16 09:07:44',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','재미있는 수업이었다. 특히 이것저것 외계인 얘기 해 주실때 재미있었다','2012-12-14 04:10:58',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','기계재료에 대해서 많은것을 알 수 있었던 수업이었다. 교수님의 목소리도 아주 멋지시다.','2012-12-14 04:12:31',10),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','목소리가 좀 나긋나긋하셔서 졸렸지만 과제하는데 공부가 많이 되었습니다.','2012-12-14 04:13:42',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','기계재료학에 대해 깊이 있는 공부를 한 것 같습니다','2012-12-14 04:15:22',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','야간에들어 수업시간이 부족해 그냥 책만 읽어주시면서 넘어갔지만, 교수님은 훌륭하시다. 센스도 있으시고 유머도 있으시고 적당히 융통성까지 있으시다. ','2012-12-18 13:11:26',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','쉽게 접근하기 힘든 과목을 어렵지 않게하면서 과제를 통해 확실히 과목을 이해할수있게 해주시다.','2012-12-19 03:29:26',10),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','감사합니다.!','2012-12-21 07:36:02',8),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','책을 주르륵 읽어주는 수업이지만 프로젝트 하나를 하고 나면 왜 책은 그냥 참고인지 알게 된다.\r\n시험방식이 센세이션이었다. 하지만 시험치면서 재밌다는 생각이 든건 처음 이었다. 종합적 사고를 하며 설계하는게 \r\n이 수업의 재미인거 같다. 그리고 교수님 언어센스 또한 좋으신듯. 하지만 정치얘기는 하지 마시길 ㅋ','2012-12-26 02:03:47',9),(737,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','열심히 가르치시고 학생들에게 유익한 정보교육을 많이 시켜주셨습니다. 과제도 의미있었습니다.','2012-12-26 04:17:54',9);
