INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (67,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','중간고사 개빡침-_-! 부셔버리겠다~!~!~!~!~!~!~!인간적으로 컴퓨터로 계산하는 수준의 문제를 내주시는 센스는 참 답없다ㅡㅡㅋㅋㅋ\r\n','2013-04-24 10:18:28',7.33333),(67,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','믿고 수강 ㄱ','2013-06-27 22:43:36',10),(67,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','들어봐 심장이 쫄깃해질꺼야~','2013-09-03 06:56:33',7.33333);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 정말 못가르치신다.........그리고 과제가 너무 많기때문에 과제양에 대해서 자신있는 사람은 수업을 듣기 바란다.........\r\n','2012-11-18 12:47:25',8.33333),(981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','설명을 진짜 왜이렇게 못하시는지... 과제는 엄청많고 시험은 엄청 쉽고... 학점은 뭔가 납득이되질 않아요 납득이!!','2012-12-08 18:29:35',4.66667),(981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','나만그런게 아니였군.. 수업 귀에 잘안들어오고.. 시험문제 뻔해서 더 안듣게되고.. 분석화학교수님답게 채점 완전 부석해서 하심.. 문제는 완전쉬운데 채점은 부석해서하심.. 학점은 마음에 들게나왔음','2013-01-05 11:01:51',1),(981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','나름 괜찮은듯? 근데 교수님이 설명을 하시는데 본인도 정리가 잘 안되시는거 같은느낌???? 모르시는게 아니라 정리가 안되는.... 음','2014-05-03 14:10:38',9),(981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너무 방글방글 웃으셔서 자꾸 기분좋게 잠들게 된다.\r\n책만보고 문제풀어보고 과제하면 시험잘볼수있는것같다. 과제문제와 같은유형으로 시험문제 내신다.\r\n시험문제 객관식도 몇개 내신다.','2014-12-30 02:44:33',9),(981,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','아무래도 생물관련이시면 생물관련강의만 하시는 편이 좋은것 같다. 이건 교수님의 무제라기보단 학교시스템의 문제라고나 할까','2015-01-07 04:15:34',7);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (351,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','상대평가되고나서 그런지 시험을 상당히 어렵게 내심...// 보고서를 무조건 잘써야 성적에 유리해짐... 내 생각에 보고서는 무조건 길게... 대충 쓰면 피봄.. 보고서 점수 확인 시켜주는데 ㅋㅋㅋ 멘붕','2013-06-26 12:36:03',7);
