INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (771,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이 수업 듣는 순간 넌 모델링의 마스터\r\n무조건 어려운거 하는거다.\r\n해석도 할 줄 알면 너의 학점은 에이쁠.','2012-12-14 08:22:07',10),(771,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정말 힘든 수업이다. 3D CAD를 다룰 줄 모른다면 절대 수강하지 말 것.\r\n한 학기 동안 팀별로 제품 하나를 정해 부품별로 모델링, 수업내용, 해석 등을 ppt로 작성하여 발표하는건데\r\n시간도 많이 뺏기고 힘들지만 다 마무리하고 나면 정말 많은 것을 배우는 이번 학기 최고의 강의다','2012-12-14 08:22:48',10),(771,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정말 많이 배웠던 과목. 내가 한만큼 성적이 나오는 과목','2013-01-20 07:36:38',10),(771,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','wf','2013-07-06 06:55:22',1);
