INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','괜찮은것 같습니다','2013-03-05 15:45:11',7),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 수업 열심히 잘 들어보세요. 수업시간에 판서 내용도 필기 열심히하시구요. 수업 안듣고 책으로 따로 공부하려고하면 이해하기 힘듭니다.(이해는 할지언정 시간이 많이 걸림) 교수님이 문제푸는 방식이 책에 있는 방법과 완전히 다른 것은 아니지만 사소한 부분에서 차이가 나는데, 저는 교수님이 풀어주시는 방법이 대개 더 쉽게 와닿았습니다. 수업방식은 (개념설명->예제풀어주고->학생들이직접풀어보기) 이런 순으로 진행되는데 학생들이 직접 풀어볼 시간을 주시는 것이 굉장히 큰 도움이 됐습니다. 솔직히 수학 수업은 듣는다고해서 머리에 바로 차곡차곡 쌓이는 것이 아니기때문에 직접 문제를 풀어보고 익히는 것이 중요한데 그 과정을 수업시간에 다 해결하도록 도와주시니 아주 좋았어요!! 이 때 문제 풀어볼 시간 주시고 교수님은 돌아다니시면서 학생들 질문 받아주십니다. 과제는 없었고 학점도 잘 주시는 것 같다.(본인이 어느정도 점수를 확보한다는 전제하에)','2013-06-22 11:28:33',10),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','직설 적으로 말하면 저 한테는 최악이였습니다. 한번찍히면 그냥 ㅅㄱ라고 생각하면 됩니다. 썰을 풀자면 저가 전날에 과음해서 수업 출석해놓고 화장실에서 좀 오래 있었습니다. 그런데 수업 시간에 돌아다닌다고 결석 처리를 하셨습니다. 사정을 말했는데도요... 저가 잘못한거라서 더는 말씀드리지 않고 그냥 그렇게 지나갔습니다. 그리고 그 다음주 시간에 설상가상으로 지각을 했습니다. 그런데 이게 무슨일인가요;;;; 수업이 끝나고 지각 처리를 하려 교수님께 갔는데 지각 체크를 하시는 척하시더니 아무 표시도 안하고 그냥 계시는 것입니다. 아무체크를 안하셨다면? 그렇쵸 결석이죠... 너무 화가나서 그냥 그 수업 다음부터 안나갔습니다. 이런 사례도 있으니 조심하세요.(아 물론 저 F받았습니다.)','2014-01-27 06:03:56',1),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','잘가르치던데','2014-02-05 01:12:10',10),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','대박사건\r\n진짜 좋았음 출석잘하고 수업시간에 열심히 들으시면 됩니다!!!!!!\r\n그럼 학점나와요','2014-03-04 03:14:47',10),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','잘가르쳐주시고 중간중간 스스로 해볼수있어서 좋았다.','2014-03-19 15:09:16',9),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제는 없고 퀴즈2번 중간 기말 시험보고 시험은 그냥 책에있는 문제다. 수업시간에 하는것만 잘 따라가면 점수 잘받는다','2014-06-22 08:16:46',1),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','밑에거 점수를 설정 안함;;; ','2014-06-22 08:17:47',9.33333),(707,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업만 따라가면 점수 잘 받을수 있다.','2014-06-23 16:17:59',9);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','하아... 열심히 따라하시면 되요... \r\n그래도 공부하면 남는것이 많은과목\r\n진짜 공부하실분이면 추천','2012-11-15 15:59:01',9),(923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 어려워서 수업한번 놓치면 힘들수도','2012-12-14 07:44:36',8),(923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수없은 그냥 ppt만 읽어주신다... 혼자하는게 나을정도로... 숙제는 매 2장씩 끝날때마다 연습문제 레포트 그리고 시험은... 진짜 공부와 다르게 예상밖이다... 놀랐다;; 개어려워잉 ㅠㅠ','2012-12-18 17:37:51',7),(923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님이 맨날 하시는 말씀이 있다. \'이 부분은 중딩때 풀던 문젠데요~\', \'이건 고딩때 배운 공식이네요~\'. 그정도로 수업이 중고딩때 배우던게 많이 나온다. 중고딩때 수학 좀 했다면 수업은 딱히 공부 안해도 잘 따라갈 수 있다. 근데 교수님이 중고딩때 배운건데도 자세히 설명하시기 때문에 오히려 수업에 집중이 안될수도 있겠다..... 과제도 연습문제 풀어오는거라 할만한 편이다. 연습문제 양이 많은 챕터는 홀수or짝수 번호만 풀어오라 하실 정도로 자비도 있으시다. 근데 다 좋은데 중간 기말이 에러다. 난 수학을 개뿔도 못하기 때문에 연습문제 거의 다 풀 수 있을 정도로 공부 해갔다. 근데 문제는 ~~를 증명해라 이런 류의 문제가 엄청 나온다. 물론 난 개망했다. 시험빼고 다 좋은 과목이었다......','2013-03-01 07:12:36',8),(923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','듣지마라. 컴퓨터공학꺼 들어라','2014-12-22 14:59:24',1),(923,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅜㅜ','2015-07-04 12:38:37',1);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (275,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','빡세지만 잘 알려주셔서 좋다. 노력하는자만 살아 남는 수업','2013-01-27 15:35:57',8.33333),(275,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','버텨 내거라','2013-07-05 05:53:15',10),(275,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','헬게이트를 여는 첫단계..','2013-07-05 14:46:56',10),(275,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','빡샙니다. 수업 따라가려면 교수님이 업로드 해주시는 실습 올라온 날짜부터 다른 실습 올라오는 날까지 죽자살자로 파서 이해해야합니다. 함수라던가 프로그램 구조는 수업에서 설명해 주시기 때문에 모르는점이 생긴다면 1차적으로 수업에서 알 수 있습니다. 만약 실습에서 모르는 부분이 생겼는데 수업에서도 따로 설명해주지 않으신다면, 그건 3D게임 프로그래밍1에서 가르치는 부분이 아니거나, 이전단계의 수준(윈플밍 등)에서 이미 그 기능을 배웠을 확률이 높습니다. 물론 궁금한점에 대해 교수님께 질문하는건 당연히 가능하지만, 교수님께 질문하기 전에 자신이 정말로 모르는 부분의 핵심적인 의문이 무엇인지 다시 한 번 생각 한 후에 질문 하시길 바랍니다. \r\n시간 많다고 실습 안하고 과제 안하면서 어영부영 하다가 마지막에 가서 한번에 하면 되겠지. 라는 생각은 버리는게 좋습니다.','2013-07-18 05:15:33',9),(275,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','헬게이트를 여는 첫단계22222222... 학교 생활 3년 중 가장 큰 고난이었음. 과제가 많고 어려움. 중간에 드랍해서 학점 어떻게 주시는지는 모르겠음ㅜ ','2014-02-05 12:56:18',7);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','3d 프로그래밍의 심화 기술에 대한 이해를 높일 수 있었고 다양한 과제를 해결해나가는 성취도가 매우 높은 수업이었다.','2012-12-26 03:14:11',10),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','........왜 B였지..','2013-01-07 14:04:58',7.66667),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 목소리가 너무 높으셔서.. 듣는건 피곤했지만\r\n3D 프로그래밍 기술에 대해 좋은 말씀도 해주시고 유익했다.\r\n\r\n수업은 이론만 나간다. 실습따윈 없다.\r\n한 학기동안 과제가 5~6개 정도 나오고 이론시간에 배운 것을 응용할 수 있는 과제들이다.\r\n시험은 1:1 면접을 한다. 3~4개의 지문에 대해 잘 설명하면 된다.\r\n\r\n하는 량이 많아서 어려운 수업으로 소문이나서 폐강이 자주되는 과목인데\r\n폐강되는게 너무 아까운 수업이다. ㅠㅠ','2013-01-22 11:14:05',9.33333),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','성취도는 높다','2013-02-01 02:11:40',1),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','따라만 간다면 진정한 고수가 될 수 있다. 당신이 게임공학과 학생이고, 프로그래머를 지망한다면 이 교수와 이 과목을 피할 수 없다. 이것은 현실이다. 그냥 들어라','2013-07-05 14:46:00',10),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','크아...','2013-10-14 03:37:56',4.66667),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','힘들다. 하지만 이거하면 취직잘된다. 경험상 느낀다.','2013-12-13 05:02:19',6.66667),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','무섭네요','2014-01-12 19:43:23',9.33333),(939,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좋아용','2015-07-04 12:29:21',7);
