INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','토너먼트식 발표수업... 과제준비나 발표준비가 빡새고... 좋은학점을 위해서는 팀 구성도 아주 중요하다... 기말시험만 잘본다고 될 과목이 아니다... 우수한 PPT인재와 발표인재를 끌어들여라... 물론 그게 당신이라면 좋은학점을 받는데는 무리가 없을것이다','2013-01-21 02:01:48',9),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','팀원이 과제 잘 안하면 철회하세요','2013-06-10 07:09:30',3),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','명강의 of 명강의\r\nBut, 과제로 한 학기 통째로 말아먹을 가능성 농후','2013-06-13 23:35:34',6.33333),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진심 명강의 껄껄... 토너먼트식 발표수업을 하는데 난 1라운드 탈락했다. 그런데 A0 받았다. 토너먼트가 다가아니다. 껄껄... 포기하지말고 하라\r\n조원이 과제안해도 혼자다하면서 강행군한다 \r\n. PPT 할줄몰라요 이거시전하면 노답 ㅡㅡ 누군 태어나면서 할줄알았나!  그리고 과제하느라 휴일 사라질 가능성 파워업','2013-06-25 10:59:50',9.33333),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학점을 떠나서 대학생활 통틀어 가장 기억에 남는 강의. 그리고 가장 좋아하는 교수님.\r\n한학기 지나고 나면 이 과목이 굉장히 재미있어 질것임.','2013-06-27 11:48:07',9.33333),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업자체는 굉장히 재밌고 유익함. 근데 팀프로젝트 자체가 굉장히 스트레스 받음 근데 한학기 끝나면 뿌듯하고 재미있음!!','2013-09-05 05:04:42',8.33333),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㄴㄴㄴㄴㄴ','2013-09-05 06:00:38',1),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','비추다.','2013-10-03 06:31:41',5),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','듣지마 듣지마듣지마 듣지마듣지마 듣지마듣지마 듣지마듣지마 듣지마듣지마 듣지마듣지마 듣지마','2013-10-05 09:18:47',1),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시간이 정말 많은 사람만 들어... 안그러면 그학기 과목들 다 망치거나 마케팅을 포기하거나 둘중하나는 해야됨..','2013-10-05 09:21:58',3.66667),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','그냥 다 전체적으로 주관적으로 평가 하는편이라 좋음','2014-09-05 07:14:32',7),(443,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','마케팅 입문에 유익함. 중간고사 잘보면 B이상은 맞을수있음. 등수도 다 매겨주심. 중간고사때 등수 높여노삼.\r\n근데 팀플 겁나빡심........ 최종피티 90장만들었음.. 근데 성적은 에이쁠나와서 뿌듯함.','2014-10-21 14:35:17',3.66667);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좀 호불호가 갈리는 교수님!! 수업방식은 PPT로 하시는데 주로 예제를 프로그램작성해서 원리를 알게끔하는 스타일이에요\r\n과제는 연습문제를 매주마다 프로그램해오는건데 좀 귀찮을 수 있지만 그래도 해나가면 자연스럽게 깨닫는부분이 있어요 \r\n학점은 잘하는사람은 잘주려고하시고 노력하는게 안보이면 학점을 안주신다고하네요..ㅠㅠ','2012-11-15 03:39:32',8.33333),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','제 개인적인 생각은 예제에 대한 코멘트를 자세히 해주셔서 모든 학생들이 수업에\r\n따라올수있게끔 해주시고요 과제도 그렇게 어렵지 않고 수업때 집중만 잘하면 \r\n풀수있을정도의 난이도에요 ','2012-11-15 13:45:11',8),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 잘 가르쳐주시나 강의하는 것이 너무 지루하여 네시간동안 힘들다.','2012-11-23 06:53:46',6.66667),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','4시간 = 아이쇼핑하는시간\r\n일주일에 한번과제함..\r\nㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠ내학점어떠카지????','2012-12-14 02:34:38',2.66667),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 목소리가 작아서 뒤에 앉으면 들리지 않는다.\r\n과제는 매주 연습문제, 시험이 손코딩이라 좀 짜증난다.','2012-12-14 05:14:50',6.33333),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진도가 너무빨라서 따라가기가 힘들다','2012-12-25 15:39:14',4.66667),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 만족도 A+ :수업진도가 매우 빠릅니다. 예습한 2시간 정도 하시고 들어가시면 제대로 쫓아가실수 있어요. 수업이 일주일에 1회고 한회당 4시간을 몰아서 하시기 때문에 하루만 빠져도 굉장히 따라가기 힘듭니다. 그리고 교수님께서 말씀하시는거 책에 나오지 않은 것도 많이 말씀하시기 때문에 커피드시고 수업들어가셔서 졸지 마시고 다 받아 적으세요. 정말 피가되고 살이 되는 말씀 하시거든요. 저는 교수님 수업을 맨 앞줄에서 들어서 상관없었는데 목소리가 작으시고 강의실은 크고 마이크도 쓰시지 않아서 3~4째 줄 이상으로 가면 안들려요 교수님 말씀이. 게다가 첫날 자신이 선택해서 앉은 자리로 한학기 내내 지정좌석제 이기 때문에 자리맡는 것도 관건. \r\n과제 만족도 A+ : 과제가 한회 수업당 4~5개 정도 됩니다. 책에 있는 과제2~3 + 교수님께서 설명해 주시는 과제 1~2 문제 정도. 과제난이도는 기본적이고 수업에서 설명하셨던거 복습정도 입니다.\r\n학점 만족도 A+ : 저는 중간고사 만점받고 기말고사 약 80정도 인데 A0받았어요. 그런데 제가 들은 수업에는 재수강하시는 선배분들이 많아서 많이 팍팍했습니다. ','2013-01-03 12:51:25',10),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅜㅜ','2013-01-22 14:58:03',6.33333),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','개인적으로 수업은 진도가 너무빠르고 객체지향 자체가 1학년이 배우기 어려운과목이라 재수강생이 많다.\r\n그러다 보니 마지막날 남은 1학년이 1명이었다.\r\n그런데 열심히 하는사람은 열심히 하는만큼 성적은 나오는것 같다. \r\n중간고사때 만점이 4명이나 있는거 보면, 성적 안나왔다고 하는사람은 공부 안한거다.','2013-03-06 15:24:47',9),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이재명 교수 수업은 그냥 강추\r\n목소리 작은것만 빼면 전자과 교수 top 5안에 들어갈듯','2013-07-05 16:58:14',10),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','목소리가 너무 작음..','2014-01-29 06:22:32',7.33333),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅅㄱ','2014-10-01 11:06:27',1),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅅㄱ','2014-12-21 20:37:59',6.66667),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','14년 2학기. 수업이 굉장히 루즈하긴하나 집중해서 들으면 전자과 탑으로 가르친다는걸 느끼게됨. 매주 과제를 내주는데 수업한 내용을 바탕으로 하면됨. 배우고자한다면 이만한 교수님이 없음. 다만 너무 수업이 지루하고 4시간동안 집중하기가 힘듬. 그리고 교수님이 엄청 권위적이시고 학생들과는 말을 잘 안섞으려하심... 성적은 짜게주시지는 않는거같음. 진짜 딱 한만큼 나오는듯.','2014-12-24 05:07:28',10),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 지루하다,,, 재수강 하는 사람들이 많다,,, ','2015-06-10 15:47:28',6),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 지루하긴한데 잘가르쳐주심 매주과제있는데 그게쫌힘들지만 과제하면서 시험공부두 되는거같음 그리고 시험 오픈북임 성적 생각보다 잘주심','2015-06-14 07:08:52',9),(807,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','호불호가 정말 갈리지만 따라만오시면 다른분들꺼 들을바에 훨씬 남는게많아요 ','2015-06-14 07:21:00',9);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅜㅜ','2012-11-20 01:24:21',1),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','걍즐','2012-12-14 07:43:14',1),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','워낙 내용이 어려워 따라가기 힘들었지만 교수님이 이해 될때까지 설명해주셔서 따라 갔던것 같다 아쉬웠던 건 수업때 필기가 너무빨라서 가끔 필기만 하고 집에가서 필기보고 독학하는 경우가 있었는데 그점만 빼면 나는 만족했다 하지만 이재명교수님과 자기 스타일이 안맞는다면 듣지마시길','2012-12-25 01:57:13',8.66667),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅠㅠㅠㅠ','2013-01-22 14:57:43',3),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','장점 : 모르는 것을 물어보면 이해될때까지 설명해주신다.  매수업시간 시작할때마다 전시간에 배웠던 내용을 복습해주신다. \r\n단점 : 필기 할 것이 많고 그만큼 외워야할 양도 많다. 시험은 필기해준 것 위주로 공부하고 거의 과제로 내줬던 부분의 필기내용을 통으로 외워야한다. 이론 전체를 써야하는 문제가 나온다. 무조건 필기를 잘해야 한다. 과제 답을 알려주시지 않아서 내가 푼게 맞는지 아닌지 잘 모를때가 많다. 그리고 기말고사되면 시험범위가 누적이다.... 원래 통신과목이 외울것도 많고 어렵지만 시험범위가 다른 교수님보다 엄청많기때문에 미리 공부해 놓지 않으면 시험기간때 힘들다. \r\n학점 : 정말 말그대로 내가 한만큼 나온다. 대충대충하면 절대 잘 안나온다. 아 그리고....대리출석은 꿈에도 꾸지 마시길... 얼굴 왠만하면 거의 외우셔서..... ','2014-01-12 02:21:37',9),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','뻐킹','2014-10-26 13:41:06',1),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학점은 짜지만 수업은 알차고 근데 너무빠르심 진도가','2014-12-16 11:33:13',8.33333),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','장점 딱 하나임\r\n이교수님 수업듣는사람 다 못해서 반이상만 이해해도 A이상\r\n나 중간 반타작에 기말던졌는데 C+','2014-12-26 07:38:41',2.66667),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','그냥 쓰레기임 . 어떻게 이런게 교수라고.','2014-12-29 09:59:36',1),(821,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','통신과목자체의 규정이라해야하나 아무튼 F제외 평균평점 3.0 이라고 하셨음. F가 수두룩 한데 베트남에서 A다 가져가니 B와 C에 몰릴수 밖에 없는 상황 30% 안에 들어도 B+,  30% 정도에 B0 를 받게되는 조금은 억울한 과목..','2015-01-06 15:41:14',1);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','야간 수업을 수강했는데 처음에 물어보시기를 적당히 할까.. 심도있게 할까.. \r\n학생들은 역시 적당한 난이도가 좋겠다고 했다.\r\n그랬더니 과제가 없다ㅋㅋㅋ 코딩이 하나도 없었다.  중간, 기말 고사는 필기, 난이도도 그렇게 높지는 않았다. ','2013-03-05 02:41:03',9.33333),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','집에서 맹글어와!\r\n남는거 NULL','2013-08-31 19:43:46',1),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','쓰레기','2013-12-27 06:30:37',1),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','2달치 수업 내용을 시험 당일날 오픈북으로 다 읽고, 이해하게 되는 매직','2014-06-10 17:53:44',8),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','남는 건 학점뿐','2014-12-28 06:34:07',6.33333),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','알고리즘은 꽤나 중요한 과목인데 이 교수님은 좀 날림으로 가르치는 느낌이다.    이론 조금 수업하시다가 예제 코드 ctrl+c,v 해보고 잘 되나 테스트 해보고 끝이다.    과제같은거도 전혀 안내주시고(공부가 안된다.) 시험문제도 집어주시기 때문에 시험 전 날 공부좀 하면 많이 풀 수 있는 수준이다.   \r\n교수님이 학생들이 롤을 하는지 인터넷을 하는지 별로 관심이 없으셔서 수업 듣는 사람이 거의 없다.   학생들이 서로 뒷자리 앉으려고 수업 30분 전에 와서 기다리기도 한다.    딴짓 안하고 공부할 자신 있으면 들어라.    (난 딴짓하고도 전날 2시간 공부했더니 잘 풀리더라. )      \r\n학점은 쪼오오오끔 잘주는 편이다. ','2015-07-06 02:51:18',6),(927,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅇㅇ','2015-07-06 06:55:24',1);
