INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','그냥 무난하다...','2012-12-23 14:10:04',7),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','딱 요거','2012-12-26 16:11:48',1),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','난..철회했음...','2013-01-06 09:06:31',1),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학점은 그냥 학생이 하는만큼 열심히 주신다.  말그대로 열심히한사람에게는 잘주면서 안한애들은  또 잘안주고..  개인적으로  이정준교수님 수업은 갠찮은데 수업하실때목소리 너무 작아서 잘안들림 ','2013-01-28 02:14:42',6.66667),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','똥','2013-12-21 12:47:08',1),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굉장히 좋다...교수님도 꽉 막힌 교수님이 아니고 어느정도 일리있게 말하면 점수 주시는 스타일....\r\n수업은 매강의마다 한챕터씩 예습하는데 한두번정도만 읽으면 다 맞힐 수 있는 수준... 코딩시험도 많이 보긴 보지만..다른 교수님보다 괜찮은것 같음\r\n시험수준도 평이하고 온화하심..\r\n그리고 코딩시험 같은 경우는 무조건 못해도 내면 점수주시니깐 내는게 좋음\r\n코딩시험 예습시험도 어떻게 보면 1점밖에 안들어가지만 굉장히 중요함 여기서 많이 점수가 갈리는거같음\r\n과제도 성실하게 내는 것 아주 중요 ','2013-12-27 14:38:17',10),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 자료구조 가르치는 교수님들이 다 빡세서 다른 자구 듣는 학생들보단 확실히 과제나 중간중간 보는 자잘한 시험들 면에서는 좋았음. 시험수준은 책만 파면 평타이상 받을 수 있는것 같음 밑에학생 말대로 코딩시험은 못했어도 뭐라도 무조건 내는편이 좋고 예습시험 과제 등등 자잘한 것들이 모여서 중간기말 자기보다 잘본애들 뒤엎을 수도 있으니 티끌모아태산을 기억해야함!','2013-12-29 17:06:17',1),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업전에 예습퀴즈봄 꼭 코딩시험도 자주보고 과제도 쫌있었습니다','2014-03-04 03:15:46',6.33333),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','잘가르치는거 같은데 평가가 왜이러는지 솔직히 잘 모르겠다','2014-06-21 07:58:21',8.33333),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','중간고사 기말고사는 할만하다. 처음1달은 c복습. 그다음부터 챕터시작할때마다 쉬운 예습 시험보고시작한다.\r\n그리고 과제같은경우는 다른 교수님들보다 수준이 좀있는편. 코딩 수시시험 2번있는데 한만큼나올듯. \r\n결과 받고 확인해보니 42명중 30% A꽉 안채워주신듯. 12명까지라고 생각했는데 10명까지만 주셨다.  12등인데 B+받음','2014-06-25 03:11:23',7.66667),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','아오 진짜 과제 졸라리 많아요..','2014-06-25 04:31:52',6.66667),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','팀프 대충하고 중간 기말만 잘봐도 성적나옴 괜히 팀프에 시간낭비함ㅡㅡ','2014-06-25 08:16:13',2.33333),(200,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com'