INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제따윈 없다,그리고 교수님이 수업에 너무 열정적으로 하시고 항상 준비를 많이 해오시는 것 같고 아시는 게 많은 것같다.\r\n그리고 학점도 항상 잘 주시려고 노력하신다.','2012-11-18 12:14:37',9.33333),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','전자과의 4대천왕 종락신은 위대하다','2012-11-22 05:44:14',6),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이종락 교수님은 말그대로 실습위주의 수업이기에 실습을 좋아하는 학생으로서 좋았다.','2012-11-23 06:47:33',8.66667),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업시간에 배운내용은 적은데 비해서 배운지식으로는 할 수 없는 실습을 진행해서 어려움이 많았다.','2012-12-14 07:28:39',7.33333),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','내용에 비해서 너무 어렵고 실습을 따라가려면 먼저공부해야한다','2012-12-25 01:22:08',4),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 혼자 하신다는게 단점... 아무도 못알아듣는데.. 혼자 하심.. 그리고 과제는 없어서 좋음 학점은 중간이고 기말이고 실습만 잘하면 점수 잘주심','2013-01-28 04:49:23',8.66667),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학생들에대한 기대치가 너무 높아서인지, 수업을 어렵게 진행\r\n과제는 없거나 간단한거만 있어서 편하고 , 실습잘하면 점수잘주심','2013-05-05 06:56:38',8),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','항상 수업에 준비를 많이 해오신다는 느낌이 들고 학생들에 대한 열정이 있으신 교수님같다.','2013-06-24 07:45:26',10),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이론수업이 좀 어렵다. 실습난이도는 쉬운데 말로만 듣고는 하기 어려워서 교수님께 다시 여쭤보거나 해야한다.','2013-07-02 01:36:34',9.66667),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','내용이 전자공학을 처음 접하는 사람에게는 어려웠으나, 그렇기에 애들이 멘붕해서 멘탈만 지키면 학점을 잘 주신다.','2013-07-06 06:10:36',8.33333),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제는 딱히 없고 수시고사인데 그거 회로 외우는거라 쉬워요.\r\n그럭저럭 괜찬아요','2013-07-06 13:14:08',8.33333),(808,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','여자들 한테 관대하심','2015-07-01 07:49:43',5.33333);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','일단 교수님 뒷끝작렬 , 셤문제 어렵지 않음 그러나  진도빼기에 급급해서 난중에 시험칠때 쩖.... 분량이 너무많음 \r\n아 그리고 교수님 수업하다가 가끔씩 산으로감 발음이 좀 부정확함 \r\n그리고 과제를 갑자기 확내줌 .......... 연습문제를 갑자기 4과정도 분량을 줌 ..','2012-12-11 16:02:09',1),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험을 책에 있는 그대로 안내서 좋음.\r\n여타 다른 시험들 책과 똑같이 내는 과목들 많은데 그러면 컨닝하는 놈들이 유리하게 됨\r\n하지만 이 수업은 언제나 뒤통수 치는 문제들이 있기때문에 아주 좋음\r\n\r\n교수가 보라는 부분만 보고 공부 얕게하면 점수 얕게 가져가는 과목\r\n나같이 보지 말라는 부분까지 공부하면 점수 잘받음 ','2012-12-12 08:52:51',10),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','합동과제가 항상 있어서 짜증난다.\r\n시험문제가 애매하며 범위가 많다\r\n','2012-12-14 04:12:59',8),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','알찬 강의였습니다. 시험은 다소 아쉬웠어요','2012-12-14 04:16:00',8.66667),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제는 매우 쉬웠지만 시험이 어려웠다.','2012-12-14 04:36:53',8),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','밑에 에프준 분과 거의 비슷한 의견입니다. \r\n그리고 밑에 에이쁠준분처럼 보지말라는 부분까지 공부안해도 충분히 점수 잘받음 ','2012-12-23 08:20:16',7),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험난이도는 완전 헬... 그치만 공부좀 하면 어느정도 알수 있는내용!! 말만 잘 연결시킴되요','2012-12-25 16:44:10',10),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅏㅏㅏ','2012-12-26 11:37:22',1),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅏㅏㅏ','2012-12-26 11:38:51',10),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅎㅎㅎㅎㅎ','2012-12-26 11:39:45',10),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','광범위한 영역을 제한된 시간내에 많이 알려주시기 위해 LTE의 속도로 진도를 나가심. 잠깐 멍때리면 안드로메다로 가버림\r\n과제는 딱히 어려울것 없음 , 학점도 하는만큼 주시니까 열심히하면 됨!','2013-01-28 11:33:25',8),(749,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','열심히 하면 됨','2013-09-06 06:12:26',9);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','목소리가 작아서 마이크를 쓰시고 수업을 하시는 데도\r\n잘 들리지 안아서 집중하기가 어렵다.\r\n수시평가랑 시험문제는 포인트만 딱딱 나오는 편이라 시험\r\n스타일은 괜찮은편이다','2012-11-15 04:40:52',7.66667),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','뭔소리를 하시는 건지......시험도 지저분하게 내신다','2012-11-15 13:04:37',6),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','한학기 내내 목이 아프다면서 들리지도 않게 수업을 하셨습니다.\r\n\r\n매번 그렇게 하시는지는 모르겠는데 제가 들었을떄는 마이크조차 안되는 떄가 많았습니다.\r\n\r\n다만 대부분의 학우들이 뒤에서 자기떄문에 맨 앞자리에서 들어서 크게 불편한건 없었는데\r\n\r\n아쉬운점은 수업시간에 시험에 대해서 크게 알려주는건 없습니다.\r\n\r\n시험 전주에 시험 뭐 나오는지 알려주며 100% 암기 시험이기때문에 서술형으로 어떻게 써 나갈지 이야기를 만들어서 외워야 합니다\r\n\r\n단답형으로 찍어주지 않는 내용이 나오는데 매번 맨 앞자리에서 수업들었지만 워낙 소리가 작아서 그런지 뭘 강조했는지 기억도 안나고 단답형은 다 틀렸습니다\r\n\r\n찍어주는거만 다 맞춰도 A는 나오니까 크게 걱정할 필요없습니다\r\n','2012-11-15 15:31:36',7.66667),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','목소리가 작다고 많이들 얘기하는데 강의실 앞에서 중간까지만 앉아도 들릴꺼라고 생각함.. 내가 그랬음.. 앞쪽에도 앉아보고 중간까지도 앉았는데 들릴껀 다들림. 다만 맨 뒤에 앉으면 잘 안들릴것같긴 함. 안들리면 앞자리 앉으세요 여러분. 수업은 피피티 위주로 하셔서 교재를 따로 안사는 학생들도 있었음. 딱히 교재없어도 될것같은 수업이긴 함. 그냥 개념을 읽고 말씀해주시는 방식. 아주 좋은방식이라고는 생각안함. 학점은 잘주신다고 유명하심.','2012-12-09 14:00:45',8),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','생물이 제일 좋아하는 과목이었던 만큼 이 수업을 듣고 실망감이 커졌습니다. 생물학이라는 과목에 대한 흥미도 떨어졌습니다.\r\n교수님 목소리는 들리긴 하나 설명도 그다지 잘하시지 않고, ppt를 읽고 설명해 주는 정도입니다. 칠판 글씨는 맨 앞에 앉아도 안보이게 쓰셔서  칠판글씨 읽는건 포기했습니다. 학점은 잘 나오긴 하나, 저에겐 정말 시간 아까웠다고 생각되는 수업중에 하나입니다.\r\n판단 잘하시길 바랍니다.','2012-12-17 16:11:32',6.33333),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','피피티 읽은것 같지 않던데 교수님 욕하기전에 자기성찰하시길','2012-12-18 16:03:03',10),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ppt만 읽어서 남는게 하나도 없는 수업, 다만 학점은 잘주는 편인거 같음','2012-12-20 07:16:24',4.33333),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이 분 자연스럽게 F 아니냐?','2012-12-25 18:37:02',1),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 그렇다 쳐도 과제가 진심 개ㅈ같음 과제로 책 전체 요약 깜지 내주는 교수가 어딨음 전문대수업도 안그러겠다.','2012-12-25 18:38:10',1),(186,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진도뺀다