INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','실습하면 일일이 돌아다니시면서 모르는거 가르쳐 주시고 학생 얼굴을 일일이 외워주시는것도 인상깊었다.\r\n학점도 한만큼보다 의외로 잘주셨다.','2012-11-15 15:53:20',9.66667),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이해를 못했을 시에 질문을 하면 일일히 챙겨주시면서 진행하시는 점을 마음에 들지만\r\n파워포인트를 읽고 딱 교재 중심으로 수업 진행이 이루어져서 지루하며 재미없는 수업이 될 수도 있습니다.\r\n프로그래밍 실력이 아닌 학점을 생각 하신다면 파워포인트와 교재에 나와있는 예제 코드를 이해 또는 외워서 쓰시면\r\n무난하게 높은 학점을 받을 수 있습니다.','2012-12-14 05:17:46',7.66667),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','약간 널널하다.','2013-03-05 02:37:11',9.33333),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','실습할때 돌아다니시면서 모르는 거 있으면 친절하게 다 도와주시고 얻을 게 많았던 수업 !!','2013-03-05 13:59:22',9),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','Zz','2013-03-05 15:29:05',1),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','Xxx','2013-03-05 15:29:11',1),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','Zzz','2013-03-05 15:29:22',1),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 다정하시고 모르는거 질문하면 친절히 답해주시고 이해 팍팍됨!\r\n강의 커리큘럼도 상당히 괜찮은듯!!','2013-06-26 10:23:29',10),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','차분하게 잘 알려주시고 학점도 어느정도만 하면 넉넉히 잘 주신다.','2013-06-26 11:56:47',10),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','책에있는걸 그대로 ppt에 옮겨서 수업하시는데 정말 정직하게 진도 나갑니다.\r\n프로젝트는 쉬운편인데 미리미리 하는편이 귀찮지는 않을거같구...     \r\n시험은 책에있는 코드만 열심히 외우면 될듯!','2013-06-27 16:55:30',8.33333),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','학생들 이름 다 외우시고 실습할때도 돌아다니시면서 봐주신다. 자료구조 이용한 과제도 스스로 한다면 상당히 코딩실력에 유익한거 같다.\r\n시험도 4번으로 나누어서 진행되서어 부담도 덜하고 중간 기말고사 기간때 다른과목에 좀 더 시간투자할수있다. 중요한 자료구조 몇개만 안다면 무리없이 따라갈수있다. ','2013-07-04 13:23:39',9),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','선행학습없이는 수업을 따라가기 매우힘듭니다. 프로젝트를 하면서 한방에 수업내용을 깨달은 과목','2013-07-17 00:53:31',7),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 사랑합니다!','2013-08-31 19:19:21',10),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','최선을 다해 열정적으로 가르치는 교수님 ','2013-12-29 12:20:10',10),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','사랑해요','2014-01-01 04:14:01',9),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','프로그래밍 수업중에 얼마없는 꿀수업','2014-06-25 19:06:03',10),(265,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','초반에 배우는 기본적인 자료구조 형태 몇 가지만 꼼꼼하게 배우고 따라간다면 강의 마무리까지 완벽하게 따라 갈 수 있음 / 중간 기말 없이 수시 고사와 프로젝트로 평가하기 때문에 시험 기간에 부담이 없음 / 진도는 유두리 있게 나가신다. 들어보면 안다','2014-07-19 18:26:02',10);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (714,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업을 재밌게하는 스타일은 아니지만 그래도 수업내용은 깔끔한편이에요 과제는 연습문제 한번씩풀었던걸로 기억하는데 공부하면서 풀만한 정도에요. 학점은 무지하게 잘주셨음 A0받았는데 A+은 두명, A0를 많이주심.','2012-11-16 05:53:05',8.33333),(714,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굿','2013-07-04 06:10:11',10),(714,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 수업최고입니다 정말 말이 필요없습니다 수학은 꼭 이분에게 들으세요 ','2013-07-19 14:59:34',10),(714,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','굿굿 괜찮음','2014-03-04 05:44:48',8.33333);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 이 교수님 상변태 수업 이해하시느 분 계세요???','2013-04-12 17:15:40',1),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업에대한 열의는 가지고 계신데 이해하기가 힘드네요...','2013-04-12 17:19:38',2.33333),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','듣지마라','2013-07-04 01:22:34',1),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과목이 어렵기도 하고, 수업 이해가 잘 안되므로 그날그날 복습은 필수임. 어떻게 입수했는지는 모르겠는데 중간고사는 족보랑 똑같이 나왔다는 소리가 있음. 족보입수해서 부정행위 열심히 준비 하는분이 상당히 눈에 띄이던데 이를 극복하기 위해서는 상당한 노력이 요구될 것으로 생각됨. 솔직히 본인도 컨닝하실거 아니면 듣지않는거 추천합니다. ','2013-07-05 13:12:21',1),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com',' 전필이니만큼 열심히 들으면 좋을듯 학점도 만족함','2013-07-17 03:41:53',9.33333),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님짱 전필이니열심히들으세요 학점도나름만족','2013-07-25 01:23:43',9),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','전필인데 어쩔 ㅋ 노력하면됨','2013-12-16 18:46:20',10),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','나쁘진 않는 데 이해가 안되는 과목임... 수업시간에 집중하는 척 쩔게 하고 반짝이는 눈과 미소지은 입만 있다면 점수는 시험본 것 보다 후하게 받을 수 있음. \r\n자기가 좋아하는 학생과 그렇지 않은 학생에 대하 편애가 조금은 있으신 편임. 개인적으로는 점수에 큰 만족을 얻지는 못했지만 괜찮다고 생각함.','2014-03-06 14:42:07',7.66667),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진짜 뭐 열심히 들어야하지만 이건 진짜 발음도 병신같고 걍 혼자 책보고 공부하는게 더 이해잘될수도 있을듯','2014-06-09 08:54:10',3.33333),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','이해안됬다는놈들은 내준 과제를 제대로 안했기때문이다. 과제로 내준 인터넷강의가 수업내용과 연관이 되므로 시간내서 과제만 했으면 아무리멍청해도 수업따라가는데 지장이없다','2014-06-19 09:45:52',9),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','그놈의 인도강의.... 전필이라서 수업내용이 어렵긴하지만 좋아요. 근데 교수님 발음이나 속도땜에 맨앞에앉아야됨ㅇㅇ','2014-06-20 07:24:17',5.33333),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 못알아듣겠고 매주 인도강의 들어야되고 수업이나 과제는 정말별로. 학점은 공부하면 나오는듯한데 채점기준이 좀이상한듯','2014-06-20 07:24:45',3.33333),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 정말 알아듣기 힘들다. 내용도 물론 너무 어렵다. 매주 인도강의 알아 듣지도 못하는걸 들어야 하고.....','2014-06-20 17:45:02',3),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','전필이라 포기할수 없다는 리스크가 있는데 교수님이 혀가 짧은분이라 발음이 정확하지가 않음. 학점은 그래도 하는만큼은 주는거 같은데 가끔 납득이 안된다는 사람들이 더러있음. IIT 교수 강의관련 말이 많은데 이게 과제때문에 했다는 사람은 성적이 당연히 안나오겠고 나름대로 신경써서 강의 들으면서 저게 뭔소린가 듣는 사람이라면 성적은 아마 좋게 나왔을 것이라고 봄.','2014-06-24 03:13:36',7),(313,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정말 공부를 어케 해야하는지 알려주는 과목이야\r\n인도강의 교수.... 이게 쫌 빡치는데  영어 잘하는 사람이면 괜찮아     \r\nisn\'t it ?  \r\n자막 띄어놓고 해석하면서 하면 강의 하나 듣는데 2~3시간 걸리거든? 근데 확실히 그렇게 하면 수업시간에 도움되\r\n그리고 책 꼭사고 , 수업시간에 이해 안되는거 책 보면 그나마 도움 많이 되니까 3학년들아 건투를 빈다\r\n\r\n\r\n\r\n','2014-06-27 02:05:47',10);
