INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님이 너무 천사같아요\r\n\r\n수업시간에만 따라가면 성적도 잘 받아요 ','2013-09-04 05:12:15',10),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','천사\r\n교수님 진짜 친절함\r\n조금 지루한감이 있지만 재밌음','2013-09-10 04:35:57',9.66667),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','제어의 신....','2013-09-24 02:54:14',10),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정말 알찬 수업....항상 감사합니다교수님','2013-09-24 02:54:33',10),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','1등가자\r\n','2013-09-24 02:54:51',10),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','졸린목소리, 그리고 차분한 설명... 수업을 잘 따라가면 문제 없는 수업.\r\n단, 화나시면(공부를 안하는 모습이 보이면) 헬게이트 열림...\r\n매주 레포트,쪽지시험...그렇다고 난이도가 쉬운건 아님.\r\n물론 헬은 공부 열심히 하는 모습만 보여드라면 안 열림','2013-10-09 15:05:40',9),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','어렵지만 그만큼 남는과목','2013-12-12 04:34:21',7.66667),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','쵝오','2015-07-02 04:59:19',9.33333),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','잘알려주세요','2015-07-02 09:05:31',9.66667),(791,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님은 착한데 수업방식이 너무 구식이고 재미가 별로 없다 변별력도 별로 없음','2015-07-02 10:33:59',1);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너므 조아염','2012-12-26 04:10:52',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','쏘쏘함. 열심히하면 좋음.','2013-02-19 12:07:10',1),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','괜찮음','2013-02-20 02:16:02',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','good','2013-03-05 04:36:45',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좋아요','2013-03-05 05:31:51',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','알기 쉽게 설명해주시고 모르면 다시 설명해주심 정말 좋다. 덕분에 진도는 살짝느릴수도^^\r\n하지만 제어공학을 처음 배우는 입장에서 들으면 정말 좋은 교수님.','2013-03-05 06:13:19',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','당연한거지만 수업잘듣고, 쪽지시험조금신경쓰고, 과제해가믄 평소에하던게 다 시험공부라서 시험기간에 진짜 조금만 공부하면 쉽다.\r\n웃픈건 그래서 그런지 다들 시험이 쉬웠다고 하는데.....\r\n구라였는지 A+은 내가 받았닼ㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋㅋ\r\n처음에 수학적인것 때문에 뭔말인지 어려울수도 있는데 그럴까봐 교수님께서 수학적인 부분을 최대한 제한하려고 하신다 겁먹지말고 잘들어라','2013-06-25 09:24:47',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','메카 강대진 교수와는 성향이 완전 상반된 교수님임.\r\n자기 지도학생을 촤대한 점수를 잘 주려고 노력하는 강대진교수와 반대로\r\n정명진 교수님은 학생들을 모두 이끌어 가려고하시는분!\r\n매 시간마다 쪽지시험을 보지만 그것이 살로 되고 피가 되는 법 !\r\n정명진 교수님의 취지도 그럴 것이다. \r\n정말 평소에 쪽지시험 공부를 한게 도움이 정말 많이 된다.\r\n중간 기말 시험때 전공 공부 하루종일 할걸 세네시간으로 줄여줌.!\r\n그리고 딱 배운 내용에서만 시험 출제! \r\n다들 문제가 쉽다고 하는데 100점 아니면 쉽다고 하지 말길..\r\n배운 내용에서만 나오기때문에 그 내용은 완벽히 숙지해야 A+받을 수 있음\r\n정말 좋은 교수님임. 우리학교에 이런 교수님이 많이 있다면 우리 학교위 발전 가능성도 더욱 높아질꺼라생각할정도.\r\n','2013-06-25 15:30:47',10),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님이 수업도 잘 가르쳐 주시고 이해도 잘 됨.. 그러나 학생들이 다 잘해서 성적따기 어려움 ㅠㅠ','2013-07-18 01:44:40',9.33333),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','중간고사 기말고사가 쉽게 나와서 학점따기가 힘들지만 수업은 다른 메카 전공과목보다 힘들지 않음 정명진교수님 너무좋다','2014-01-11 09:06:55',7.66667),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','메카라면 꼭 들어야 할 수업','2014-02-05 01:23:50',8.33333),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','진정으로 메카트로닉스공학과의 발전을 몸소 보여주시며 학생들을 리더해주시는 메카의 리더..\r\n3학년에서만 볼수있다는게 아쉽습니다.. \r\n수강신청의 경쟁률을 뚫는 당신에겐 신에 가호가...','2014-03-05 12:23:17',1),(90,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','정명진 교수님 최고','2014-03-25 07:55:04',10);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (694,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제가 너무 힘든거 같다. 다른 물리학실험반은 보고서를 타이핑으로 하지만 이 반은 손글씨로 작성한다. 과제로 너무 의미없이 시간을 보내는것 같다.','2013-01-11 03:22:20',5),(694,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제가 손글씨라 힘들긴 하지만 실험 설명은 잘해주시는 편. 그래서 결과보고서 쓰긴 좋다. 시험공부도 같이되고.\r\n일반물리학실험2는 다른교수님이 타이핑형식이래서 들었는데...진짜 후회했다. 가르쳐주시는것도 없었거든. 그래서 오히려 정부성 교수님이 그리웠다. 손글씨가 힘든걸 감수할만큼. 보고서 열심히 쓰고 이론 잘보면 점수 잘 주신다.','2013-01-13 16:27:07',9),(694,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업에 열정적이시고 매우 도움이 많이된다\r\n하지만 과제가 너무 힘들다\r\n학점은 한거에 비해서 퍼주는수준이다 최고','2013-01-13 18:11:54',8.33333),(694,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','한글을 읽고 쓸 줄아는 학생들에게 추천\r\n','2013-01-16 23:55:11',8.33333);
