INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (1149,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','음 학생들을 위하는 마음이 크시고,수업에 열정이 가득하시고 쉽게 가르쳐주신다.만족스러운 수업','2012-12-25 01:40:11',9),(1149,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업을 책만 읽으시고, 지루한감이 없지않아 있다. 또한 수업시간안에 해결해야하는 과제는 많은데 시간은 촉박해서, 자신이 뭘 배웠는지 모르지만 열심히 하고 나온 느낌은 든다.','2013-01-09 15:46:09',7.33333),(1149,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너무 지루한 수업이었다. \r\n영어 실력은 늘엇지만 다양한면에서 아쉬웠다\r\n특히 학점이 짜다','2013-07-04 06:06:42',6.33333),(1149,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 초반에는 숙제 많이 내주시는데 가면 갈수록 숙제도 없습니다.\r\n영어실력 어느 정도 있다면 최고인듯 합니다. ','2013-12-27 09:47:38',8.66667),(1149,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','뭔가 비는 점수가 있으면 채울 수 있는 기회 많이 주시고 잘 챙겨주심  엄마st','2014-01-30 06:19:18',7.66667);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔찍히 수업들으면서 얻어가는게 많은수업이 있는반면 그렇지 안은수업도 많은데\r\n\r\n정용석교수님 수업은 진짜 얻는게 많은 수업입니다. 열정도 대단하시고.... 한학기동안 많은걸 배웠네요\r\n','2013-06-26 09:06:45',10),(311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너무 수업을 열심히 나가셔서 시험범위가 너무 광범위해집니다','2013-07-04 14:02:24',7),(311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','b수업외적으로도d\r\n범위는넓은데 시험엔 중요하게 설명하신것만나와요','2013-07-04 14:33:39',10),(311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님 재밌으심 ㅎㅎㅎ  과제도 철강에 흥미갖게끔 해주시고, 물론 수업내용도 충분히 따라올 수있게 가르치심.\r\n학점도 자기가 얼마나 노력하느냐에 따라 후하게 점수 주셔서, 전선이라 고민하시는 사람있으면 고민말고 바로 수강신청하길.\r\n정말정말 추천. 한마디로 얻어가는게 많은 수업','2013-07-06 08:01:53',10),(311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험난이도는 보통 적당히 변별력있고 너무 쉽지도 않고 너무 어렵지도 않다 학점은 노력하는만큼 나오는 것 같다. 난 열심히 해서 A+ 받았다. 시험은 집어준 거 위주로만 보면 충분한듯','2014-06-23 14:19:47',9),(311,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좋은 수업이에요!','2014-06-25 07:37:06',9.33333);
