INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','공부를 할 수 밖에 없다... 깐깐징어 같은 사람...','2013-04-10 04:40:17',10),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','졸라게 어렵다','2013-04-17 05:39:20',5.33333),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','과제가 매주 있다. 수업에서 배운 것으로도 할 수 있는 난이도지만 양적으로나 질적으로 부담된다.\r\n특히 기말과제는 최고다.','2013-06-28 10:49:17',6),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','허헌 교수님 수업은 전자회로 비주얼 두가지 들었는데 둘 다 수업은 참 좋다.\r\n다만 우리에게 요구하는게 너무 많아서 힘들다..\r\n수업이 도움되는건 사실이지만 심적으로는 부담될걸?\r\n학점은 잘주시니 듣는걸 추천','2013-07-02 06:40:30',7),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','아아...메카에 이분에 없었다면 학생들의 전공지식은 메카 삼대장에게 처절히 짓밟혔을거야.... 허느님!! 짱짱맨!!!\r\n\r\n원래 2학기치 과목이었는데 이번에 한학기로 줄어든거라 진도가 매우 빨랏지만,\r\n특유의 카리스마로 수업에 집중할수밖에 없고, 정말 중요한 내용을 잘 설명하는 능력이 뛰어나심.\r\n2주에 한번꼴로 과제가 있지만 수업내용만 이해하면 다 할수있고 과제 하다보면 저절로 공부가 됨.\r\n\r\n필기시험 두번에 기말 텀프 한번인데,\r\n필기시험은 c++보다는 c내용이 많이 나온다. 어차피 앞으로 뭘하든지 c가 기본이니까 c복습 잘해놓으면 잘볼수 있음\r\n기말텀프로 mfc 게임 만들었는데 많이 힘들지만 그동안 열심히 해왔고 시간투자 좀하면 거의 완성 할수있다.\r\n\r\n매우 중요한 과목인만큼 빡세고 확실하게 강의 하시니까, 공부안할거면 듣지마\r\n노력한다면 너의 프로그래밍 실력은 몇단게 업그레이드! 안한다면 f\r\n\r\n다시한번 허느님 찬양!\r\n\r\n','2013-07-04 06:25:33',9.66667),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','기말텀프 난이도가 말도안된다 ','2013-07-18 11:56:39',7),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','스파르타식 C 기초 닦기','2014-03-01 01:59:51',8.33333),(95,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅇㅎ','2014-03-02 06:44:21',7);
INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','매우까칠할것처럼보이지만열심히하는자에게는잘챙겨주신다\r\n하지만   뭔가부족한  .............2%','2012-12-13 14:48:32',9),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','기초 알려주고 시험문제는 미적분급','2012-12-11 18:16:55',1),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 내용이해도 측면에서는 정말 만족합니다. 또한 과제를 꼼꼼하게 풀게 해주어 수업 만족도는 만족합니다.  시험문제가 조금 어려워서 힘들었습니다.','2012-12-14 08:03:02',8.66667),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 만족함.\r\nbut 아무리 절대 평가여도 너무 난이도 높은 시험문제 출제','2012-12-14 08:03:31',8),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','제점수는요 C+입니다','2012-12-23 13:06:44',6),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','하는만큼 돌아오고 자기 방에 찾아가서 물어보면 친절하게 답해주십니다. 호불호가 갈리지만 저는 좋았습니다.','2012-12-24 04:56:02',9.33333),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','한거에 비해 너무 과분한 점수를 주신다','2012-12-24 08:16:32',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 메카 이번 2학년 야간중에 허헌교수님 빼고는 다 수준이하 쓰레기들이지\r\n교수하기전엔 스펙도 있고 날고 기었다고 생각하지만 지금 직업이 교수면 가르침이 뭔지는 알아야 될것 아닌가.\r\n교수들이 말이야 셤시간에 여자애한테는 답을 알려주고 변별력 없는 채점기준과 책보다 못한 가르침을 주면 \r\n이게 대학인가? 중학교인가? 고딩교사들은 그래도 맡은 과목은 좀 하더이다 젊은교사는 \r\n어찌 됐던간에 2학기는 돈내고 다닐 학기라기엔 대학이라는 이름이 부끄러울 정도였다.','2012-12-24 09:43:59',7.33333),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업들으면서 자기분야에 확신있어 보이는 교수 중 한분이라고 생각합니다.  그냥 시간이나 때우고 배우는건지 안배우는건지 하는 수업은 아닙니다. 다만 앞자리에 앉으세요... 목소리 잘 안들립니다. ','2013-01-22 16:49:06',9.33333),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','메카 저학년 다른 교수님들이 너무 수준이하라서 더욱 돋보이는 교수님\r\n이정도는 되야 교수님 소리 들을 자격이 있지....\r\n강의도 괜찮고 거의 매주 과제가 있고 수시고사도 두번 보지만\r\n과목자체가 벼락치기로 가능한 과목이 아니기에 좋은 방식 같음','2013-03-03 19:50:57',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험이 어려운건 함정','2013-06-24 07:38:05',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','허느님이 보우하사 ','2013-09-04 00:35:38',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험이 어려워도 다들 어려워서 분별력 생겨 더 좋은거 아닌감','2013-12-12 04:56:30',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업이 빡세고 한 챕터가 끝날때마다 과제가 나옴. 시험도 어렵다고 하는데 수업시간에 집중하면 뭐가 중요한지 다 알게되고 기본 원리를 이해하고 과제만 스스로하면 학점 따기에는 매우 좋음. 2학년때 메카에서 제대로된 교수님 만난듯. 다시 한번 강조하지만 수업은 빡셈.','2013-12-25 07:14:32',9.66667),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','강의 매우 잘 설명해주심. 신호에 비해 더 준비를 많이해오신다고 느끼는건 나만인가....그래서인지 매우 만족하고 들었음. 시험 문제가 예상 못한 부분에서 나온다 해도 애들이 감을 못잡으면 공부한 학생들은 알 수 있게 중간에 힌트도 내주심. 그리고 솔직히 과제가 빡세다는거 이해 못하겠음. 적당히 할만큼만 내주신거같은데...필요한 문제만 골라서 풀어오게 내주시니까. 정말 존경하고싶은 교수님. 모든 학생을 그래도 끌고가려고 노력하시는 모습도 보기 좋고, 찾아갈때마다 강의에 대한 피드백을 받고싶어하는 것도 좋고. 강의 준비를 정말 학생들을 위해 열심히 해주신다고 느낀다.','2014-01-26 14:54:40',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','강의에 집중할수있는 몇안되는 수업\r\n할려는 의지가 조금 있다면 꽤 많은걸 얻을수있는 수업\r\n허나 다들 열심히 하기에 좀 더 노력해야 원하는 학점을 받을수있음','2014-01-29 06:03:45',8),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','허헌 교수님 츤데레','2014-03-01 01:56:48',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','너 이거 뭐야?? 몰라?? 그 뒤에. 이거 뭐야?? 몰라?? 뒤에. 이거. 뒤에. 옆 옆 옆. 공부안했어? (과제확인) 너 이거 풀었는데? 뭐냐. 시험봐야겟다.','2014-06-25 07:49:09',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','츤츤 데레 츤 데레 츤츤','2014-06-25 07:50:34',10),(787,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','헬 오브 헬. 수업시간 탈모 해야합니다. 수업시간에 화장실 갈 수 없습니다. \r\n수업내용은 최고 입니다.(앞자리 앉으세요.)','2014-06-25 08:08:26',7);
