INSERT INTO evaluation (lecture_id, member_id, method, task, comment, write_date, score) VALUES (1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','1. 쉽게 알려주려 하였으나 자료준비 미숙 수업방식 미숙.\r\n2. 사칙연산 알려주고 미적분하라는 느낌의 과제물\r\n3. 어려웠던만큼 독학을 피나게 했지만 그에 상응하는 성적을 받을지는 미지수...','2012-12-26 04:22:35',7),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','솔직히 지루하지만 내용이 재밌어서 집중해서 들으면 그럭저럭 괜찮음. 과제가 챕터마다 나오는데... 수업땐 이론만 알려주시다가 갑자기 과제 문제풀려니 좀 어렵게 느껴짐. 나름대로 열심히 공부한다고 했는데 생각보다 점수가 높진 않았음.','2013-01-20 14:11:23',7.66667),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업 자체는 화학, 재료공학쪽을 배우는 과목이고, 전기재료2를 들어봐야 알겠지만 재료1은 참고 들을만 해요. 중간고사 까지는 화학 했던 학생은 쉽게쉽게 가고 기말고사 부분은 화학이랑 기계공학 융합한 수업에 외워야 할건 수두룩함\r\n시험은 그냥 닥치고 죄다 외워야 점수 잘맞아요','2013-01-20 15:40:48',7.66667),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','교수님이 일단 휴강을 잘 내심. 수업은 피피티위주 과제가 좀 어려움. 시험은 과제하고 책에서 많이 나옴','2013-01-31 07:39:13',8),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','외우고 외우고 또 외우고... 말 그대로 재료쪽이라 생소할 수 있음. 피피티 위주로(분량 어마어마함) 수업 하시고 과제는 챕터마다 내주시는데 수업은 이론-과제는 계산 위주라 어렵게 느껴짐. 시험 유형은 과제에 있는 계산문제와 개념을 묻는 문제가 많이 나옴. 사실 수업이 좀 졸려요... 그치만 이겨낼 만한 정도의 수준! 열심히 한다면 좋은 성적도 기대 할 수 있음','2013-07-08 14:40:31',9),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','ㅇㅇ','2013-09-05 07:50:52',1),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','좋음 왜그럼','2013-09-05 07:51:27',10),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업은 PPT 위주이나, 전기공학을 전공하는 학생이라면 과제난이도가 상당할 수 있음. 주의하길.  시험은 공부한대로 나옴.','2013-12-26 02:33:09',5.33333),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','열심히 들어도 기본 개념이 없으면 힘든 과목','2014-02-05 04:41:27',5.33333),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','수업자체에서 많은걸 알려주시려고함 그러나 따라가기가 힘들다','2014-06-21 13:03:47',8.66667),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','시험은 개념위주 계산위주 지만 정말 수식자체가 어렵고 개념도 가면갈수록 어려워진다 하지만 모두다 어렵기 때문에 하기나름','2014-06-24 17:40:09',9.66667),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','화학잘하면 쉬우나 왜배우는건지모르겠다','2014-09-03 23:27:17',1),(1110,1,'벙커에서 작성된 강의평가입니다','www.kpubunker.com','어려움','2014-09-05 11:19:10',1);
